mapper.update:
update product
set pro_name = #{pro_name}
	, pro_big = #{pro_big}
	, pro_mid = #{pro_mid}
	, pro_age = #{pro_age}
	, pro_stock = #{pro_stock}
	, pro_price = #{pro_price}
	, pro_dc = #{pro_dc}


		, pro_dc_strt = '0000-00-00'
		, pro_dc_end = '0000-00-00'


		, pro_dc_strt = #{pro_dc_strt}
		, pro_dc_end = #{pro_dc_end}


	, pro_ship = #{pro_ship}
	, pro_ship_free = #{pro_ship_free}
	, pro_ctnts = #{pro_ctnts}

		, pro_thum_pic = #{pro_thum_pic}
		, pro_thum_path = #{pro_thum_path}


		, pro_prdt_pic = #{pro_prdt_pic}
		, pro_prdt_path = #{pro_prdt_path}

where pro_no = #{pro_no}
and m_no = #{m_no}

mapper.delete:
delete from product
where  pro_no = #{pro_no}
and m_no = #{m_no}

mapper.select:
select count(p.pro_no) cnt from product p ,member m where p.m_no = m.m_no and m.m_no = #{m_no}
and p.pro_name like concat('%', #{searchWord}, '%')
and p.pro_no like concat('%', #{searchWord}, '%')

