mapper.update:
update basket 
set b_stock = #{b_stock} 
where b_no = #{b_no} 
and m_no = #{m_no}

mapper.select:
select m.m_no, p.pro_no , op.ordpro_no , o.ord_no , d.addr_no
,b.b_no , b.b_date , b.b_stock ,p.pro_thum_pic ,p.pro_thum_path ,p.pro_name ,op.ordpro_price ,op.ordpro_stock ,o.ord_pay ,o.ord_dc_pay, o.ord_sum ,d.addr_name ,d.recipient_name ,d.tel ,d.post_code ,d.addr1 ,d.addr2
from member m, product p, ordpro op, ord o, delivery_addr d, basket b where op.pro_no = p.pro_no and o.ord_no = op.ord_no and b.m_no = #{m_no}
